import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import InputSearchItem from "../InputSearchItem/InputSearchItem";
import ReactDOM from "react-dom";
import { Formik, Field, Form } from "formik";
import "./InputSearch.css";
import classNames from "classnames";
function InputSearch() {
  const [isShow, setIsShow] = useState(false);
  const [input1, setInput1] = useState(false);
  const [input2, setInput2] = useState(false);
  const handleClick = () => {
    setInput1(!input1);
    setInput2(!input2);
    setIsShow((oldState) => !oldState);
  };
  return (
    <>
      {/* <form className="FormSearch" id="FormSearch">
        <button
          className={classNames("btn-search", { input2: input2 })}
          onClick={handleClick}
        >
          <FontAwesomeIcon icon="search" />{" "}
        </button>
        <input
          className={classNames("input", { input1: input1 })}
          type="text"
          placeholder="abc"
          onClick={handleClick}
        />
        {isShow && (
          <>
            <InputSearchItem />
          </>
        )}
      </form> */}
      <Formik
        initialValues={{
          search: "",
        }}
        onSubmit={async (values) => {
          await new Promise((r) => setTimeout(r, 500));
          alert(JSON.stringify(values, null, 2));
        }}
      >
        <Form>
          <button
            className={classNames("btn-search", { input2: input2 })}
            onClick={handleClick}
          >
            <FontAwesomeIcon icon="search" />{" "}
          </button>

          <Field
            className={classNames("input", { input1: input1 })}
            type="text"
            placeholder="abc"
            onClick={handleClick}
          />
           {isShow && (
          <>
            <InputSearchItem />
          </>
        )}
        </Form>
      </Formik>
    </>
  );
}

export default InputSearch;
