import React, { useState, useRef, useEffect } from "react";
import BtnSearch from "../ButtonSearch/BtnSearch";
import InputSearchItem from "../InputSearchItem/InputSearchItem";
import "./InputSearch.css";
import classNames from "classnames";
function InputSearch() {
  const ref = useRef();
  const [isShow, setIsShow] = useState(false);
  const [input1, setInput1] = useState(false);
  
  useEffect(() => {
    const checkIfClickedOutSide = (e) => {
      if (isShow && ref.current && !ref.current.contains(e.target)) {
        setIsShow(false);
      }
    };
    document.addEventListener("mousedown", checkIfClickedOutSide);
    return () => {
      document.removeEventListener("mousedown", checkIfClickedOutSide);
    };
  }, [isShow]);
  const handleClick = () => {
    setInput1(!input1);
    setIsShow((oldState) => !oldState);
  };
  return (
    <>
      <form className="FormSearch" id="FormSearch">
        <BtnSearch onClick={() => setIsShow((oldState) => !oldState)} />
        <input
         className={classNames("input",{input1: input1})}
          type="text"
          placeholder="abc" 
          onClick={handleClick}
        />
        {isShow && (
          <>
            <InputSearchItem />
          </>
        )}
      </form>
    </>
  );
}

export default InputSearch;
