//tao ra state cap nhap xu ly
import * as types from "../actions/type";
//khoi tao state : mac dinh ban dau
const inittialState = {
  count: 0,
};
export const couterReducer = (state = inittialState, action) => {
  switch (action.type) {
    case types.INCREMENT:
      return {
        ...state,
        count: action.payload.val + 1,
      };
    case types.DECREMENT:
      return {
        ...state,
        count: action.payload.val - 1,
      };
    default:
      return state;
  }
};
