import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React from "react";
import "./Pagination.css";
function Pagination({postPerPage,totalPost,paginate}) {
  const totalPages = Math.ceil(totalPost/postPerPage);
  const pageNumber= [];
  for(let i =0; i<=totalPages; i++){
    pageNumber.push[i];
  }
  return (
    <>
      <div className="Pagination">
        <button
          className="PaginationIcon"
          disabled={paginate(pageNumber.map(number=>(number +1))) <= 1}
          onClick={() => paginate(pageNumber.map(number=>(number +1)))}
        >
          <FontAwesomeIcon icon="chevron-left" />
        </button>
        <button
          className="PaginationIcon"
          disabled={paginate(pageNumber.map(number=>(number +1))) >= totalPages}
          onClick={() => paginate(pageNumber.map(number=>(number +1)))}
        >
          <FontAwesomeIcon icon="chevron-right" />
        </button>
      </div>
    </>
  );
}
export default Pagination;
