import React, { Component } from 'react';
import  firebase  from '@firebase/app'
import "firebase/firestore";
import "firebase/auth";
firebase.initializeApp({
  apiKey: 'AIzaSyBM3zZlVi0C9ygN-WL1HqP7bsgCkNZi2mY',
  authDomain: 'PROJECT_ID_CỦA_BẠN.firebaseapp.com',
  projectId: 'reactjs-zingmp3'
});
function Test(){
    var db = firebase.firestore();
    db.collection("Lam").get().then((querySnapshot) => {
    querySnapshot.forEach((doc) => {
    console.log(doc.thutu,"-", doc.data().Nhac,"-", doc.data().anhien);
    });
    });
}


export default Test;
