import "./PlayControl.css";
import  Dongthoigian from "./song/Dongthoigian.mp3"
import Nguoiyeugiandon from "./song/Nguoiyeugiandon.mp3";
import Vanyeudaythoi from "./song/Vanyeudaythoi.mp3";
import React, { useState, useRef } from "react";
import TimeSlider from "react-input-slider";
import "./App.scss";
import TetImg from "./images/tet.jpg";
import PrevIcon from "./icons/PrevIcon";
import NextIcon from "./icons/NextIcon";
import PauseIcon from "./icons/PauseIcon";
import PlayIcon from "./icons/PlayIcon";
import audios from "./audios";

// import PlayControlItem from "../../Atoms/PlayControlItem/PlayControlItem";
// import PlaySong from "../../Atoms/PlaySong/PlaySong";
// import PlayRight from "../../Atoms/PlayRight/PlayRight";

const active=[
    {
        name: "random"
    },
    {
        name: "backward"
    },
    {
        name: "play-circle"
    },
    {
        name: "forward"
    },
    {
        name: "retweet"
    },
    
];
const control=[
    {
        name: "video"
    },
    {
        name: "microphone-alt"
    },
    {
        name: "window-restore"
    },
    {
        name: "volume-up"
    },
    {
        name: "list-ul"
    }
];
const listSong = [
    {
        src: Vanyeudaythoi,
        name : "Vẫn Yêu đấy thôi",
        isPlay : false,
        img: "https://photo-resize-zmp3.zadn.vn/w240_r1x1_jpeg/cover/5/1/7/c/517ca58e0bb720d2e469e96259ef2bdd.jpg",
        singers :"Chi dan"
    },
    {
        src: Dongthoigian,
        name : "Vẫn Yêu đấy thôi",
        isPlay : false,
        img: "https://photo-resize-zmp3.zadn.vn/w240_r1x1_jpeg/cover/3/5/4/2/3542386e138893792cd904405d6a4190.jpg",
        singers :"12"
    },
    {
        src: Nguoiyeugiandon,
        name : "Vẫn Yêu đấy thôi",
        isPlay : false,
        img: "https://photo-resize-zmp3.zadn.vn/w240_r1x1_jpeg/cover/5/f/2/d/5f2d9c8e7e39c0776a254980a26ec63d.jpg",
        singers :"12"
    }
];


const App = () => {
  const audioRef = useRef();
  const [audioIndex, setAudioIndex] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isPlay, setPlay] = useState(false);

  const handleLoadedData = () => {
    setDuration(audioRef.current.duration);
    if (isPlay) audioRef.current.play();
  };

  const handlePausePlayClick = () => {
    if (isPlay) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setPlay(!isPlay);
  };

  const handleTimeSliderChange = ({ x }) => {
    audioRef.current.currentTime = x;
    setCurrentTime(x);

    if (!isPlay) {
      setPlay(true);
      audioRef.current.play();
    }
  };

  return (
    <div className="App">
      <img className="Song-Thumbnail" src={TetImg} alt="tet" />
      <h2 className="Song-Title">{audios[audioIndex].title}</h2>
      <p className="Singer">{audios[audioIndex].artist}</p>
      <div className="Control-Button-Group">
        <div
          className="Prev-Button"
          onClick={() => setAudioIndex((audioIndex - 1) % audios.length)}
        >
          <PrevIcon />
        </div>
        <div className="Pause-Play-Button" onClick={handlePausePlayClick}>
          {isPlay ? <PauseIcon /> : <PlayIcon />}
        </div>
        <div
          className="Next-Button"
          onClick={() => setAudioIndex((audioIndex + 1) % audios.length)}
        >
          <NextIcon />
        </div>
      </div>
      <TimeSlider
        axis="x"
        xmax={duration}
        x={currentTime}
        onChange={handleTimeSliderChange}
        styles={{
          track: {
            backgroundColor: "#e3e3e3",
            height: "2px",
          },
          active: {
            backgroundColor: "#333",
            height: "2px",
          },
          thumb: {
            marginTop: "-3px",
            width: "8px",
            height: "8px",
            backgroundColor: "#333",
            borderRadius: 0,
          },
        }}
      />
      <audio
        ref={audioRef}
        src={audios[audioIndex].src}
        onLoadedData={handleLoadedData}
        onTimeUpdate={() => setCurrentTime(audioRef.current.currentTime)}
        onEnded={() => setPlay(false)}
      />
    </div>
  );
};

export default App;
