import React from 'react';

class Event extends React.Component {
    constructor(props){
        super(props);
        this.state={
            isToggleOn: true
        };
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick = ()=>{
        console.log("this í: ", this);
        this.setState(prevState=>({
            isToggleOn: !prevState.isToggleOn
        }));
    }
    shoot = (a,b) => {
        alert(a+b);
        alert(b.type);
        alert("Hi , My name Event React!");
    }
    form = ()=>{
        // console.log("submit thanh cong");
        const numbers = [1, 2, 3, 4, 5];
        const listItems = numbers.map((numbers) =>
            <li>{numbers}</li>
        );
        return <ul>{this.listItems}</ul>
    }
   
        
      render() {
        return (
            <>
            <button onClick={(ev)=> this.shoot("Lam xinh",ev)}>Click Me!</button>
            {/* hoac co the su dung ham bind() */}
            {/* <button onClick={this.shoot.bind(this, "Lamlam","xinh gai")}>Click!</button> */}
            <form onSubmit={this.form()}>
                <button type="submit">Submit</button>
                
            </form>
            <button onClick={()=>this.handleClick()}>
                {this.state.isToggleOn? 'ON':'OFF'}
            </button>
            </>
        );
      }
}
