import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./Theme.css";
import db from "../../FirebaseConfig";
const icon = {
  name: "Theme",
  icon: "tshirt",
};
function Theme() {
  const [theme, setTheme] = useState([]);
  useEffect(() => {
    db.collection("theme")
      .get()
      .then((snapshot) => {
        snapshot.docs.forEach((element) => {
          setTheme(snapshot.docs.map((e) => e.data()));
        });
      });
  }, []);
  if (theme.length === 0) return null;
  return (
    <>
      <div className="Theme-Main">
        <FontAwesomeIcon className={icon.name} icon={icon.icon} />
        <div className="Interface">
          <div className="exit">
            <p className="Name">Giao diá»‡n</p>
            <FontAwesomeIcon className="icon" icon="times" />
          </div>
          {theme.map((item) => (
            <>
              <p className="theme-title">{item.id}</p>
              <div className="theme">
                {item.list.map((x) => (
                  <>
                    <div className="theme-content">
                      <img className="theme-img" alt="" src={x.img} />
                      <p className="theme-text">{x.title}</p>
                    </div>
                  </>
                ))}
              </div>
            </>
          ))}
        </div>
      </div>
    </>
  );
}

export default Theme;
