import React from 'react';
import BtnSearch from '../ButtonSearch/BtnSearch';
import ListSearch from '../List-Search/List-Search';
import './InputSearch.css';
// import ListSearch from '../List-Search/List-Search';
// import InputSearchItem from '../InputSearchItem/InputSearchItem';

class InputSearch extends React.Component {
    constructor(props){
        super(props);
        this.state={
            value: "",
            isShow: false
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit =  this.handleSubmit.bind(this);
    }
    handleChange = (e)=>{
        this.setState({
            // value: e.target.value,
            isShow:true
        });

    }
    handleSubmit(e){
        alert("A name was submitted: "+ this.state.value);
        e.preventDefault(); 
    }
    render() {
        return (
            <>
               <form className="FormSearch" onSubmit={this.handleSubmit}>
                   <BtnSearch/>
                   <input
                        className="input" 
                        type="text"
                        placeholder="abc"
                        // value={this.state.value} 
                        onClick={this.handleChange()}
                   />
                   <div>
                   { this.state.isShow ?<ListSearch/> :false }    
                   </div>
                </form> 
            </>
        );
    }
}

export default InputSearch;
