import React from "react";
import PlayControlItem from "../../Atoms/PlayControlItem/PlayControlItem";
import PlaySong from "../../Atoms/PlaySong/PlaySong";
const active=[
    {
        name: "random"
    },
    {
        name: "backward"
    },
    {
        name: "play-circle"
    },
    {
        name: "forward"
    },
    {
        name: "retweet"
    },
    
];
const control=[
    {
        name: "video"
    },
    {
        name: "microphone-alt"
    },
    {
        name: "window-restore"
    },
    {
        name: "volume-up"
    },
    {
        name: "list-ul"
    }
];
const listSong = [
    {
        name : "abc",
        isPlay : false,
        img: "https://photo-resize-zmp3.zadn.vn/w240_r1x1_jpeg/cover/5/1/7/c/517ca58e0bb720d2e469e96259ef2bdd.jpg",
        singers :"12"
    },
    {
        name : "abc",
        isPlay : false,
        img: "https://photo-resize-zmp3.zadn.vn/w240_r1x1_jpeg/cover/3/5/4/2/3542386e138893792cd904405d6a4190.jpg",
        singers :"12"
    },
    {
        name : "abc",
        isPlay : false,
        img: "https://photo-resize-zmp3.zadn.vn/w240_r1x1_jpeg/cover/5/f/2/d/5f2d9c8e7e39c0776a254980a26ec63d.jpg",
        singers :"12"
    },
    {
        name : "abc",
        isPlay : false,
        img: "https://photo-resize-zmp3.zadn.vn/w240_r1x1_jpeg/cover/7/8/8/5/7885c2ade26fc2dd33a2c33638f5f72a.jpg",
        singers :"12"
    },
];

function PlayControl(){
    return(
        <>
        <div className="PlayControl">
            <PlaySong song={listSong}/>
            {active.map(item => (
                <>
                <PlayControlItem active={item}/>
                </>
            ))}
        </div>
        
            
        </>
    )
}
export default PlayControl;
