import React, { useEffect, useState } from "react";
import Pagination from "../Pagination/Pagination";
import CarouselItem from "../CarouselItem/CarouselItem";
import db from "../../FirebaseConfig";
import { Skeleton } from "antd";

function CarouselSongNew() {
  const [song, setSongs] = useState([]);
  const list = [];
  useEffect(
    // eslint-disable-next-line react-hooks/exhaustive-deps
    () => {
      
      db.collection("His")
        .get()
        .then((snapshot) => {
          console.log(snapshot.docs);
          setSongs(snapshot.docs.map(e => e.data()))
          // // setSongs(snapshot.docs.map(doc=>({...doc.data(),id:doc.id
          // // })))
          // snapshot.docs.forEach((element) => {
          //   song.push(element.data());
          // });
          // console.log(song);
        });
    },
    []
  );
  // useEffect(
  //   () =>{
  //     db.collection('His').onSnapshot(snapshot=>{

  //     setSongs(snapshot.docs.map(doc=>({...doc.data(),id:doc.id
  //     })))
  //     // console.log(song);
  //     }
  // )}, [])
  if(song.length === 0) return <Skeleton/>
  return (
    < >
      <div className="CarouselTitle">
        <p className="CarouselText">{song[0].title}</p>
        <Pagination />
      </div>
      <div className="Carousel">
        {song[0].list.map((item, i) => (
          <CarouselItem key={i} item={item} />
        ))}
      </div>
    </>
  );
}
export default CarouselSongNew;
