import React, { useEffect, useState } from "react";
import Pagination from "../Pagination/Pagination";
import CarouselItem from "../CarouselItem/CarouselItem";
import db from "../../FirebaseConfig";

function CarouselSongNew() {
  const [song, setSongs] = useState([]);
  // useEffect(
  //   () =>{
  //     db.collection('history').onSnapshot(snapshot=>{
  //     setSongs(snapshot.docs.map(doc=>({...doc.data(),id:doc.id
  //     })))
  //     }
  // )}, [])
  useEffect(() => {
    db.collection("His").onSnapshot((snapshot) => {
      setSongs(snapshot.docs.map(doc => ({...doc.data(), id: doc.id })))
    });
  }, [song]);
  return (
    <>
      <div className="CarouselTitle">
        <p className="CarouselText">{song[0].title}</p>
        <Pagination />
      </div>
      <div className="Carousel">
        {song[0].list.map((item) => (
          <CarouselItem item={item} />
        ))}
      </div>
    </>
  );
}
export default CarouselSongNew;
