class Clock extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      date: new Date(),
      color: "red"
    };
  }

  componentDidMount(){
    this.timeID = setInterval(
      ()=> this.setState(
            {
              date:new Date(),
              color: "yellow"
            }
          )
    ,1000);
  }

  componentWillUnmount(){
    clearInterval(this.timeID);
  }

  render() {
    return (
      <div>
          <h1>Hello, World!,{sum()}</h1>
          <h2>It is {this.state.date.toLocaleTimeString()}</h2>
          <p>color: {this.state.color}</p>
      </div>
    );
  }
}
