import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./Theme.css";
import db from "../../FirebaseConfig";
const icon = {
  name: "Theme",
  icon: "tshirt",
};
function Theme() {
  const [theme, setTheme] = useState([]);
  useEffect(() => {
    db.collection("theme")
      .get()
      .then((snapshot) => {
        snapshot.docs.forEach((element) => {
          setTheme(snapshot.docs.map((e) => e.data()));
        });
      });
  }, []);
  if (theme.length === 0) return null;
  return (
    <>
      <div className="">
        <FontAwesomeIcon className={icon.name} icon={icon.icon} />
        <div className="Interface">
          <p>Giao diá»‡n</p>
          {theme.map((item) => (
            <div className="theme">
              {item.list.map((x) => (
                <>
                  <p className="title-theme">{x.id}</p>
                  <div>
                    <div>
                      <img alt="" src={x.img} />
                      <p>{x.text}</p>
                    </div>
                  </div>
                </>
              ))}
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Theme;
