import React, { useEffect, useState } from "react";
import "./CarouselMusic.css";
import CarouselItem from "../../Atoms/CarouselItem/CarouselItem";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import RadioItem from "../../Atoms/RadioItem/RadioItem";
import Pagination from "../../Atoms/Pagination/Pagination";
import CarouselSongNew from "../../Atoms/CarouselSongNew/CarouselSongNew";
import "firebase/firestore";
import db from "../../FirebaseConfig";
function CarouselMusic() {
  const [song, setSongs] = useState([]);
  const [sug, setSuggestions] = useState([]);
  const [audio, setAudio] = useState([]);
  useEffect(() => {
    db.collection("His")
      .get()
      .then((snapshot) => {
        snapshot.docs.forEach((element) => {
          song.push(element.data());
        });
      });
  }, []);
  useEffect(() => {
    db.collection("radio")
      .get()
      .then((snapshot) => {
        snapshot.docs.forEach((element) => {
          audio.push(element.data());
        });
      });
  }, []);
  useEffect(() => {
    db.collection("suggestions")
      .get()
      .then((snapshot) => {
        snapshot.docs.forEach((element) => {
          sug.push(element.data());
        });
      });
  }, []);
  if (song?.length === 0 && audio?.length === 0 && sug?.length === 0)
    return <div>Loading..</div>;
  return (
    <>
      <div className="CarouselTitle">
        <p className="CarouselText">{song[0].title} <FontAwesomeIcon icon=""/></p>
        <Pagination />
      </div>
      <div className="Carousel">
        {song[0].list.map((item) => (
          <CarouselItem item={item} />
        ))}
      </div>
      <div className="CarouselTitle">
        <p className="CarouselText">{song[0].title}</p>
        <Pagination />
      </div>
      <div className="Carousel">
        {song[0].list.map((item) => (
          <CarouselItem item={item} />
        ))}
      </div>
      <div className="CarouselTitle">
        <p className="CarouselText">{sug[0].title}</p>
        <Pagination />
      </div>
      <div className="Carousel">
        {sug[0].list.map((item) => (
          <CarouselItem item={item} />
        ))}
      </div>
      <div className="CarouselTitle">
        <p className="CarouselText">{sug[0].title}</p>
        <Pagination />
      </div>
      <div className="Carousel">
        {sug[0].list.map((item) => (
          <CarouselItem item={item} />
        ))}
      </div>
      <CarouselSongNew />
      <div className="CarouselTitle">
        <p className="CarouselText">{audio[0].title}</p>
        <Pagination />
      </div>
      <div className="Carousel">
        {audio[0].list.map((item) => (
          <RadioItem item={item} />
        ))}
      </div>
    </>
  );
}

export default CarouselMusic;
